copy.scriptmapper:
// replace dots with underscores in JS file names, retaining the suffix
if (/\.js$/.test(source)) {
  source = source.substr(0, source.length - 3).replace(/\./g, '_') + '.js';
}
self.addMappedName(source);

then.script:
		    load("nashorn:mozilla_compat.js");
                    importClass(org.apache.tools.ant.util.Base64Converter);
                    importClass(java.nio.charset.StandardCharsets);
                    importClass(java.nio.file.Files);
                    importClass(java.nio.file.Paths);
                    importClass(java.nio.file.StandardOpenOption);

                    var conv = new Base64Converter();
                    var filename_patt = /smiley-(.*)\.(\w+)/;

                    var mimemap = {
                        gif: 'image/gif',
                        png: 'image/png',
                        jpg: 'image/jpeg',
                    };

                    var emoticons =
                        self.getProject().getReference("tinymce.emoticons.ref");
                    var dstfile =
                        self.getProject().getProperty("tinymce.emoticon.file");
                    var iter = emoticons.iterator();

                    // build up a map of emoticon name -> image data URI
                    var map = {};

                    while (iter.hasNext()) {
                        var path = iter.next().getFile().toPath();
                        var match =
                            String(path.getFileName()).match(filename_patt);

                        var data = conv.encode(Files.readAllBytes(path));
                        var name = match[1];
                        var type = mimemap[match[2]];

                        map[name] =
                            String(java.lang.String.format("data:%s;base64,%s",
                                                           [type, data]));
                    }

                    // write it to disk as a loadable JavaScript source file
                    writer = Files.newBufferedWriter(Paths.get(dstfile, []),
                                                     StandardCharsets.UTF_8,
                                                     [StandardOpenOption.TRUNCATE_EXISTING,
                                                      StandardOpenOption.CREATE]);

                    writer.write("tinyMCE.emoticon_map = ");
                    writer.write(JSON.stringify(map, null, 4));
                    writer.write(";\n");
                    writer.close();
